import React, { useState, useEffect } from 'react'
import PlayerDataService from "../services/playersService";
const TournamentResults = (props) => {
    const initialPlayerState = {
        _id: null,
        name: "",
    }
    
    const initialPlayerList = []
    const [playersList, setPlayersList] = useState(initialPlayerList);
    const [player, setPlayer] = useState(initialPlayerState);

    const getPlayersList = arr => {
        for(let i=0; i < props.scorecards.length; i++){
            PlayerDataService.get(props.scorecards[i].playerId)
                .then(response => {
                    setPlayer(response.data)
                    arr.push(player)
                })
        }
        setPlayersList(playersList)
    }

    useEffect(() => {
        getPlayersList()
    })

    return(
        <div>
            <div>
                Yes
            </div>
            <div>
                {playersList.map(play => {
                    return(<p>{play.name}</p>)
                })}
                {/* {props.scorecards.map(score => {
                    return(
                        <p>{score.playerId}</p>
                    )
                })} */}
            </div>
        </div>
    )
}
export default TournamentResults
