import React, {useState, useEffect} from "react";
import PlayerDataService from "../../services/playersService";
import {Link} from "react-router-dom";

const SpecificPlayer = props => {
    const initialPlayerState = {
        id: null,
        name: "",
        player_scorecards: [],
    };

    const [player, setPlayer] = useState(initialPlayerState);

    const getPlayer = id => {
        PlayerDataService.get(id)
            .then(response => {
                setPlayer(response.data);
                console.log(response.data);
            })
            .catch(e => {
                console.log(e)
            });
    };

    useEffect(() => {
        getPlayer(props.match.params.id);
    }, [props.match.params.id]);

    return(
        <div>
            {player ? (
                <div>
                    <h4>Player ID: {player._id}</h4>
                    <h5>Player Name: {player.name}</h5>
                    {player.player_scorecards.length > 0 ? (
                        <>
                            <div>Scores!</div>
                            <table>
                                <tr>
                                    <th>Par 3</th>
                                    <th>Par 4</th>
                                    <th>Par 5</th>
                                </tr>
                            </table>
                        </>
                    ) : (
                        <div>No Scores Yet</div>
                    )}
                </div>
            ) : (
                <p>No Player Yet</p>
            )}
        </div>
    )
}
export default SpecificPlayer;
