import React, { useState, useEffect } from 'react'

const TournamentResults = (props) => {
    const initialPlayerState = {
        id: null,
        name: "",
    }

    const [currentPlayer, setCurrentPlayer] = useState(initialPlayerState);
    
    const initialPlayerList = []
    const [playersList, setPlayersList] = useState(initialPlayerList);

    const getPlayersList = arr => {
        for(let i=0; i < props.scorecards.length; i++){
            PlayerDataService.get(props.scorecards[i].playerId)
                .then(response => {
                    setCurrentPlayer(response.data)
                    currentPlayer.push(arr)
                })
        }
        setPlayersList(playersList)
    }

    const getCurrentPlayer = id => {
        PlayerDataService.get(id)
            .then(response => {
                setCurrentPlayer(response.data)
            })
            .catch(e => {
                console.log(e)
            })
    }

    useEffect(() => {
        getPlayersList()
    })

    return(
        <div>
            <div>
                Yes
            </div>
            <div>
                {playersList.map(player => {
                    return(
                        <p>{player.name}</p>
                    )
                })}
            </div>
        </div>
    )
}
export default TournamentResults
