import React, {useState, useEffect} from "react";
import PlayerDataService from "../../services/playersService";
import {Link} from "react-router-dom";
import PlayerStatistics from "../../components/playerStatistics";

const SpecificPlayer = props => {
    const initialPlayerState = {
        id: null,
        name: "",
        player_scorecards: [],
    };

    const [player, setPlayer] = useState(initialPlayerState);
    let numOfPar3s=0;
    let numOfPar4s=0;
    const numOfPar5s=0;
    const scoreOnPar3s=0;
    const scoreOnPar4s=0;
    const scoreOnPar5s=0;
    const getPlayer = id => {
        PlayerDataService.get(id)
            .then(response => {
                setPlayer(response.data);
                console.log(response.data);
            })
            .catch(e => {
                console.log(e)
            });
        
    };

    function renderScores(){
        const tableData = [];
        let numOfPar3s=0;
        for(let i = 0; i < player.player_scorecards.par_holes.length; i++){
            if(player.player_scorecards.par_holes[i] === 3){
                numOfPar3s++;
            }
        }
        tableData.push(<td>{numOfPar3s}</td>)
        return tableData;
    }


    useEffect(() => {
        getPlayer(props.match.params.id);
    }, [props.match.params.id]);

    const calculateNumberOfPar3s = () => {
        let numOfPar3s=0;
        for(let i=0; i < player.player_scorecards.par_holes.length; i++){
            if(player.player_scorecards.par_holes[i] === 3){
                numOfPar3s++;
            }
        }
        return numOfPar3s;
    }

    function calculateNumberOfPar4s(){
        let numOfPar4s=0;
        for(let i=0; i < player.player_scorecards.par_holes.length; i++){
            if(player.player_scorecards.par_holes[i] === 4){
                numOfPar4s++;
            }
        }
        return numOfPar4s;
    }

    function calculateNumberOfPar5s(){
        let numOfPar5s=0;
        for(let i=0; i < player.player_scorecards.par_holes.length; i++){
            if(player.player_scorecards.par_holes[i] === 5){
                numOfPar5s++;
            }
        }
        return numOfPar5s;
    }

    return(
        <div>
            {player ? (
                <div>
                    <h4>Player ID: {player._id}</h4>
                    <h5>Player Name: {player.name}</h5>
                    {player.player_scorecards.length > 0 ? (
                        <>
                            <div>Scores!</div>
                            <PlayerStatistics scorecards={player.player_scorecards} />
                        </>
                    ) : (
                        <div>No Scores Yet</div>
                    )}
                </div>
            ) : (
                <p>No Player Yet</p>
            )}
        </div>
    )
}
export default SpecificPlayer;
